package com.viettel.etc.controllers;import com.viettel.etc.dto.DoctorPriceDTO;import com.viettel.etc.dto.DoctorPriceRequestDTO;import com.viettel.etc.services.BookingDurationsConsultantDoctorsService;import com.viettel.etc.utils.Constants;import com.viettel.etc.utils.FnCommon;import com.viettel.etc.utils.TeleCareException;import com.viettel.etc.xlibrary.core.constants.FunctionCommon;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.security.core.Authentication;import org.springframework.security.core.annotation.AuthenticationPrincipal;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestHeader;import org.springframework.web.bind.annotation.RestController;import javax.validation.Valid;import java.util.Optional;/** * Autogen class: * * @author ToolGen * @date Mon Sep 14 15:13:32 ICT 2020 */@RestControllerpublic class BookingDurationsConsultantDoctorsController {	@Autowired	private BookingDurationsConsultantDoctorsService bookingDurationsConsultantDoctorsService;	/**	 * Danh sach cac thoi luong kem gia co the dat tu van voi bac si	 *	 * @param authentication: thong tin nguoi dung	 * @param dataParams      params client	 * @return	 */	@GetMapping(value = "booking-durations-consultant-doctors", produces = MediaType.APPLICATION_JSON_VALUE)	public ResponseEntity<Object> getBookingDurationsConsultantDoctors(@AuthenticationPrincipal Authentication authentication, DoctorPriceDTO dataParams,																	   @RequestHeader("lang")Optional<String>lang) {        /*        ==========================================================        authenEntity: user info and role        dataParams: danh sach bien client co the truyen len        ==========================================================        */		String language = lang.isPresent() ? lang.get() : Constants.VIETNAM_CODE;		dataParams.setLanguage(language);		try {			Object resultObj = bookingDurationsConsultantDoctorsService.getBookingDurationsConsultantDoctors(dataParams);			return new ResponseEntity<>(FunctionCommon.responseToClient(resultObj), HttpStatus.OK);		} catch (TeleCareException e) {			e.printStackTrace();			return new ResponseEntity<>(FnCommon.responseToClient(e), HttpStatus.OK);		}	}	@GetMapping(value = "booking-durations-consultant-doctors-v2", produces = MediaType.APPLICATION_JSON_VALUE)	public ResponseEntity<Object> getBookingDurationsConsultantDoctorsV2( @Valid DoctorPriceRequestDTO dataParams,																	   @RequestHeader("lang")Optional<String>lang) {        /*        ==========================================================        authenEntity: user info and role        dataParams: danh sach bien client co the truyen len        ==========================================================        */		String language = lang.orElse(Constants.VIETNAM_CODE);		dataParams.setLanguage(language);		Object resultObj = bookingDurationsConsultantDoctorsService.getBookingDurationsConsultantDoctorsV2(dataParams);		return new ResponseEntity<>(FnCommon.responseToClient(resultObj), HttpStatus.OK);	}}