package com.viettel.etc.controllers;import com.viettel.etc.dto.DoctorsCommentsDTO;import com.viettel.etc.services.DoctorsCommentsService;import com.viettel.etc.xlibrary.core.constants.FunctionCommon;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RestController;/** * Autogen class: * * @author ToolGen * @date Wed Dec 23 14:47:05 ICT 2020 */@RestControllerpublic class DoctorsCommentsController {	@Autowired	private DoctorsCommentsService doctorsCommentsService;	/**	 * Danh sach comment bac si	 *	 * @param dataParams params client	 * @return	 */	@GetMapping(value = "{doctorId}/comments", produces = MediaType.APPLICATION_JSON_VALUE)	public ResponseEntity<Object> getDoctorComments(@PathVariable Integer doctorId, DoctorsCommentsDTO dataParams) {        /*        ==========================================================        authenEntity: user info and role        dataParams: danh sach bien client co the truyen len        ==========================================================        */		dataParams.setDoctorId(doctorId);		Object resultObj = doctorsCommentsService.getDoctorComments(dataParams);		return new ResponseEntity<>(FunctionCommon.responseToClient(resultObj), HttpStatus.OK);	}}