package com.viettel.etc.services.tables;import com.viettel.etc.repositories.tables.BookingInformationsRepositoryJPA;import com.viettel.etc.utils.Constants;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.time.LocalTime;import java.util.Date;/** * Autogen class: Create Service For Table Name Booking_informations * * @author ToolGen * @date Wed Aug 19 21:30:23 ICT 2020 */@Servicepublic class BookingInformationsServiceJPA {	@Autowired	BookingInformationsRepositoryJPA booking_informations;	public Integer countNumberBooking(LocalTime registerTime, Date registerDate, Integer doctorId, String healthfacilitiesCode) {		return booking_informations.countByRegisterTimeAndRegisterDateAndDoctorIdAndHealthfacilitiesCodeAndIsActiveAndIsDelete(				registerTime, registerDate, doctorId, healthfacilitiesCode, Constants.IS_ACTIVE, Constants.IS_NOT_DELETE		);	}	public boolean existsByMedicalexaminationDateAndDoctorId(Date date, int doctorId) {		return booking_informations.countByMedicalexaminationDateAndDoctorIdAndIsDeleteAndIsActive(date, doctorId, Constants.IS_NOT_DELETE, Constants.IS_ACTIVE) > 0 ? true : false;	}}